import React from 'react';
import './play.css';

class Play extends React.Component {
  constructor(props) {
    super(props);
    this.state = {game:""}

    this.letsPlay = this.letsPlay.bind(this)
  }

  letsPlay() {
    return (
        <div>
            <h1>Let's Play!</h1>
            <div className="games"><button>Numbers</button></div>
            <div className="games"><button>Letters</button></div>
            <div className="games"><button>Colors</button></div>
            <div className="games"><button>Shapes</button></div>
        </div>
    );
  }

  render() {
    return (
        <div>
        {this.state.game === "Numbers" ? <h1>Numbers</h1> : this.letsPlay()}
        </div>
    );
  }
}

export default Play;
